SELECT dataset_table_task_log.*, dataset_table_task.name, dataset_table.name as dataset_name FROM dataset_table_task_log LEFT JOIN dataset_table_task ON dataset_table_task_log.task_id = dataset_table_task.id LEFT JOIN dataset_table ON dataset_table_task_log.table_id = dataset_table.id
where 1=1
and dataset_table_task.id = #{id}
and dataset_table_task_log.status in
#{status}
and dataset_table.id in
#{tid}
and (dataset_table_task.last_exec_time between #{lastExecTime.[0]} and #{lastExecTime.[1]})
and dataset_table_task.name like concat('%', #{keyword}, '%')
ORDER BY dataset_table_task_log.create_time desc

SELECT dataset_table_task_log.*, dataset_table_task.name, dataset_table.name as dataset_name FROM dataset_table_task_log LEFT JOIN dataset_table_task ON dataset_table_task_log.task_id = dataset_table_task.id LEFT JOIN dataset_table ON dataset_table_task_log.table_id = dataset_table.id where 1=1
and dataset_table_task.id = #{id}
and dataset_table_task_log.status in
#{status}
and dataset_table.id in
#{tid}
and (dataset_table_task.last_exec_time between #{lastExecTime.[0]} and #{lastExecTime.[1]})
and dataset_table_task.name like concat('%', #{keyword}, '%')
ORDER BY dataset_table_task_log.create_time desc

SELECT dataset_table.name as table_name, 'grant,manage,use' as `privileges`,dataset_table_task.* , qrtz_triggers.NEXT_FIRE_TIME FROM dataset_table_task left join dataset_table on dataset_table.id=dataset_table_task.table_id left join qrtz_triggers on dataset_table_task.id=qrtz_triggers.TRIGGER_NAME where 1=1
and dataset_table_task.rate in
#{rateItem}
and dataset_table_task.status in
#{statusItem}
and dataset_table_task.last_exec_status in
#{lastExecStatusItem}
and dataset_table.id in
#{tid}
and dataset_table_task.id = #{id}
and (dataset_table_task.last_exec_time between #{lastExecTime.[0]} and #{lastExecTime.[1]})
and dataset_table_task.name like concat('%', #{keyword}, '%')
order by dataset_table_task.create_time desc

SELECT dataset_table.name as table_name, get_auths(dataset_table_task.table_id,'dataset', #{userId}) as `privileges`,dataset_table_task.* , qrtz_triggers.NEXT_FIRE_TIME FROM dataset_table_task left join dataset_table on dataset_table.id=dataset_table_task.table_id left join qrtz_triggers on dataset_table_task.id=qrtz_triggers.TRIGGER_NAME
where 1=1
and dataset_table_task.rate in
#{rateItem}
and dataset_table_task.status in
#{statusItem}
and dataset_table_task.last_exec_status in
#{lastExecStatusItem}
and dataset_table_task.id = #{id}
and dataset_table.id in
#{tid}
